import * as eccrypto from 'eccrypto'; // todo
const webSocketUrl = 'ws://192.168.51.154:8080/websocket/msg';

// automatical request
const responseObject = {
  cmd: 'handshake',
  msg: {
    birthNumber: '120493904239048',
    documentNumber: '271498327498',
    publicKey: '23oiroi2joi23jrio23j'
  },
  signature: '99r8329r8j2398fj93f2j983f2j'
}

class WebID {

  webSocket;
  handshakeCmd;
  handshakeCallback;
  handshakeData;
  message;
  signature;
  dataCmd;

  constructor() {
    this.webSocket = new WebSocket(webSocketUrl);
    this.setListeners();
    this.handshakeCmd = false;
    this.handshakeCallback = null;
    this.handshakeData = null;
    this.dataCmd = null;
    this.message = null;
    this.signature = null;
  }

  login(cb) {
    if (this.handshakeData !== null) {
      cb(null, this.handshakeData.message, this.handshakeData.signature);
    } else {
      if (this.webSocket.readyState === 1) {
        this.handshakeCmd = true;
        this.webSocket.send({ cmd: 'handshake', msg: {} });
        this.handshakeCallback = cb;
      } else {
        cb({ msg: 'connection error', readyState: this.webSocket.readyState }, null, null);
      }

    }
  }

  async handshake(message, signature) {
    if (this.handshakeCmd) {
      this.validateMessage(message, signature)
        .then(() => this.handshakeCallback(null, message, signature))
        .catch((err) => { console.log(err); });
    } else {
      this.validateMessage(message, signature)
        .then(() => { this.handshakeData = { message, signature }; })
        .catch((err) => { console.log(err); });
    }
  }

  getData(messaage, signature) {
    // todo
  }

  setListeners() {
    this.webSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      switch (data.cmd) {
        case 'handshake':
          this.handshake(data.msg, data.signature);
          break;
        case 'data':
          this.getData(data.msg, data.signature);
          break;
        default:
          throw new Error('unknown cmd');
      }
    }
  }

  validateMessage(message, signature) {
    return new Promise((resolve, reject) => {
      const publicKey = message.publicKey;
      const signedMessage = JSON.stringify(message);
      const bufferedMessage = Buffer.from(signedMessage, 'utf8');
      eccrypto.verify(publicKey, bufferedMessage, signature).then(() => {
        return resolve(true);
      }).catch(() => {
        return reject(false);
      });
    })
  }

}

export default WebID;

